mapper.update:
update tbl_board
set replycnt = replycnt+#{amount}
where bno =#{bno}

mapper.select:
select * from(
select /*+INDEX_DESC(tbl_board pk_board)*/
rownum rn,bno,title,content,writer,regdate,updateDate,replyCnt
from tbl_board
where

 rownum <=#{pageNum}*#{amount}
)
where rn>(#{pageNum}-1)*#{amount}
